"use strict";

exports.generateAll = function () {
    var fs = require("fs");
    fs.readdir(process.cwd() + "/schema/", function (error, schemas) {
        if (error) {
            console.log("Some error occur while reading the schemas, Error: ", error);
        } else if (schemas && schemas.length > 0) {
            var generateSchemaNames = [];
            if (process.argv.length > 2) {
                var Schemas = process.argv.slice(2);

            } else {
                generateSchemaNames = schemas;
            }
            require("../domain/GenerateAll").generateDomain(generateSchemaNames);
            require("../service/GenerateAll").generateService(generateSchemaNames);
            require("../controller/GenerateAll").generateController(generateSchemaNames);
            require("../ngController/GenerateAll").generateNgController(generateSchemaNames);
            require("../ngRoute/GenerateAll").generateNgRoute(generateSchemaNames);
            require("../ngView/GenerateAll").generateNgView(generateSchemaNames);
        } else {
            console.log("No Schema found");
        }
    });
};