"use strict";

exports.generateAll = function () {
    var fs = require("fs");
    var _ = require("underscore");
    var CONSTANTS = require("../enum/CONSTANTS");
    fs.readdir(process.cwd() + "/schema/", function (error, schemas) {
        if (error) {
            console.log("Some error occur while reading the schemas, Error: ", error);
        } else if (schemas && schemas.length > 0) {
            var servicesDirectory = process.cwd() + "/service/";
            var generateServices = function () {
                schemas.forEach(function (schemaName) {
                    var domainName = schemaName.replace(/^(.)(.*)(.json)$/, function (schemaName, g1, g2) {
                        return g1.toUpperCase() + g2;
                    });
                    var newDocumentData = "{";
                    var schema = require(process.cwd() + "/schema/" + schemaName);
                    _.each(schema, function (value, key) {
                        newDocumentData += ("\n" + CONSTANTS.TAB + CONSTANTS.TAB + key + ": " + domainName.toLowerCase() + "CO." + key + ",");
                    });
                    newDocumentData += "}";
                    newDocumentData = newDocumentData.replace(/,}/, "\n" + CONSTANTS.TAB + "}");
                    fs.readFile(__dirname + "/" + "Content.txt", function (error, data) {
                        var service = data.toString().replace(/{{domainName}}/g, domainName)
                            .replace(/{{domainNameInSmallLetter}}/g, domainName.toLowerCase())
                            .replace(/{{newDocument}}/, newDocumentData);
                        fs.writeFile(servicesDirectory + domainName + "Service.js", service, function (error, data) {
                            if (error) {
                                console.log("Some Error Occur at the time of generating the Services, Error=", error);
                            } else {
                                console.log("Services Generated");
                            }
                        });
                    });
                });
            };
            fs.exists(servicesDirectory, function (exists) {
                if (exists) {
                    generateServices();
                } else {
                    fs.mkdir(servicesDirectory, function (error, dir) {
                        if (error) {
                            console.log("Error Occur at the time of creating the directive(" + servicesDirectory + "). Error= ", error);
                        } else {
                            generateServices();
                        }
                    });
                }
            });
        } else {
            console.log("No Schema found");
        }
    });
};